import React from 'react'
import { useState, useEffect } from 'react'
import Blog from "../../components/Blog/Blog"
import { MdKeyboardArrowRight } from "react-icons/md"
import { Link } from "react-router-dom"
import img from "../../images/oldMan.png"
import './Blogs.modulus.css'
import Authentication from "../auth/Authentication/Authentication"

const Blogs = () => {
    const [blogs, setBlogs] = useState()
    const data = [
        { info: { name: "Ahmed", job: "Fullstack Developer" }, img: img, quote: "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Et, ad, voluptas, iste cum pariatur deserunt aut dolorum ut dolores tempore qui. Quo harum repudiandae beatae quaerat, officia dolores repellendus laboriosam." }, { info: { name: "Mostafa", job: "Frontend Developer" }, img: img, quote: "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Et, ad, voluptas, iste cum pariatur deserunt aut dolorum ut dolores tempore qui. Quo harum repudiandae beatae quaerat, officia dolores repellendus laboriosam." }
    ]
    useEffect(() => {
        if (!Authentication) {
            setBlogs(data)
        }
    }, [])
    return (
        <>
            <section className="title">
                <div className="contain">
                    <div className="overlay"></div>
                    <div className="content">
                        <h1>All Blogs</h1>
                        <h5>
                            <Link to="/">Home</Link>
                            <MdKeyboardArrowRight />
                            <p>Blogs</p>
                        </h5>
                    </div>
                </div>
            </section>
            <section className="blogs">
                <div className="contain">
                    {
                        blogs ? blogs.map((blog) => (
                            (<Blog data={blog} />)
                        )) : (<h3> No Blogs Found </h3>)
                    }
                </div>
            </section>
        </>
    )
}

export default Blogs
