import React from 'react'
import { useState, useEffect, useCallback } from 'react'
import Blog from "../../components/Blog/Blog"
import { MdKeyboardArrowRight } from "react-icons/md"
import { Link } from "react-router-dom"
import img1 from "../../images/oldMan.png"
import img2 from "../../images/youngMan.jfif"
import { collection, getDocs } from "firebase/firestore"
import maleImg from "../../images/male.png"
import femaleImg from "../../images/female.jpg"
import { database } from "../../firebase"
import './Blogs.modulus.css'

const data = [
    { info: { name: "Ahmed", email: "someone@gmail.com" }, img: img1, quote: "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Et, ad, voluptas, iste cum pariatur deserunt aut dolorum ut dolores tempore qui. Quo harum repudiandae beatae quaerat, officia dolores repellendus laboriosam.", duration: { date: "Sometime", time: "SomeTime" } }, { info: { name: "Mostafa", email: "someone@gmail.com" }, img: img2, quote: "Lorem ipsum dolor sit amet, consectetur adipisicing elit. Et, ad, voluptas, iste cum pariatur deserunt aut dolorum ut dolores tempore qui. Quo harum repudiandae beatae quaerat, officia dolores repellendus laboriosam.", duration: { date: "Sometime", time: "SomeTime" } }
]

const Blogs = () => {
    const [blogs, setBlogs] = useState(data)
    const collectionRefBlogs = collection(database, 'blogs')

    const getUserBlogs = useCallback(() => {
        getDocs(collectionRefBlogs).then((response) => {
            let allBlogs = []
            response.docs.map((blog) => {
                allBlogs.push(blog.data())
            })
            setBlogs(allBlogs)
        }).catch((error) => {
            console.log(error)
        })
    }, [collectionRefBlogs])

    useEffect(() => {
        getUserBlogs()
    }, [])
    return (
        <>
            <section className="title">
                <div className="contain">
                    <div className="overlay"></div>
                    <div className="content">
                        <h1>All Blogs</h1>
                        <h5>
                            <Link to="/">Home</Link>
                            <MdKeyboardArrowRight />
                            <p>Blogs</p>
                        </h5>
                    </div>
                </div>
            </section>
            <section className="blogs">
                <div className="contain">
                    {
                        blogs ? blogs.sort((a, b) => new Date(a.date) - new Date(b.date)).map((blog, i) => (
                            <Blog key={i} data={{ img: (blog.photo ? blog.photo : (blog.idGender === 'male' ? maleImg : femaleImg)), info: { name: blog.username, email: blog.email }, quote: blog.blog, duration: { date: blog.date, time: blog.time } }} />
                        )) : (<h3> No Blogs Found </h3>)
                    }
                </div>
            </section>
        </>
    )
}

export default Blogs
