import React, { useEffect, useState, useRef } from 'react'
import { useOutletContext } from "react-router-dom"
import "./Profile.modulus.css"
import oldMan from "../../images/oldMan.png"

const Profile = () => {
    const blogText = useRef()
    const authUser = useOutletContext()
    const [blogs, setBLogs] = useState()

    const submitBlog = async (e) => {
        e.preventDefault()
        const res = await fetch("https://webghoul-system-a5360-default-rtdb.firebaseio.com/")
        const data = await res.json()
        const blog = blogText.current.innerHTML
        // try {
        //     addDoc(ref, { users: blog })
        // }
        // catch (error) {
        //     console.log(error)
        // }
        return data
    }

    return (
        <section className="profile">
            <div className="contain">
                <div className="back">
                    <picture>
                        <img src={oldMan} alt="oldMan" />
                    </picture>
                </div>
                <div className="info">
                    <h2 className="name">Ahmed</h2>
                    <h3 className='email'>{authUser ? authUser.email : ""}</h3>
                </div>
                <div className="create-blog">
                    <h3 className='head'>Create your blog</h3>
                    <form>
                        <textarea ref={blogText} name="create-blog" id="create-blog" placeholder='blogging here....'></textarea>
                        <button onClick={submitBlog} type='submit'>Create</button>
                    </form>
                </div>
                <div className="blogs">
                    <h3 className='head'>Your Blogs</h3>
                    <article>
                        {blogs ? "" : ""}
                    </article>
                </div>
            </div>
        </section>
    )
}

export default Profile
