import React, { useEffect, useState, useRef } from 'react'
import { useOutletContext } from "react-router-dom"
import "./Profile.modulus.css"
import oldMan from "../../images/oldMan.png"
import { database } from "../../firebase"
import { collection, addDoc, getDocs, doc, updateDoc, deleteDoc } from "firebase/firestore"

const Profile = () => {
    const blogText = useRef()
    const authUser = useOutletContext()
    const [blogs, setBLogs] = useState()
    const collectionRefBlogs = collection(database, 'blogs')
    const collectionRefUsers = collection(database, 'users')

    const [userName, setUserName] = useState()

    const addBlog = () => {
        addDoc(collectionRefBlogs, {
            email: "webGhoulDev@gmail.com",
            username: "mahmoud"
        }).then(() => {
            console.log("Done")
        }).catch((error) => {
            console.log(error)
        })
    }

    const submitBlog = async (e) => {
        e.preventDefault()
        const blog = blogText.current.innerHTML
    }
    useEffect(() => {
        getDocs(collectionRef)
            .then((response) => {
                console.log(response.docs.map((item) => {
                    return { ...item.data(), id: item.id }
                }))
            }).catch((error) => {
                alert(error.message)
            })
        setUserName()
    })

    return (
        <section className="profile">
            <div className="contain">
                <div className="back">
                    <picture>
                        <img src={oldMan} alt="oldMan" />
                    </picture>
                </div>
                <div className="info">
                    <h2 className="name">{userName}</h2>
                    <h3 className='email'>{authUser ? authUser.email : ""}</h3>
                </div>
                <div className="create-blog">
                    <h3 className='head'>Create your blog</h3>
                    <form>
                        <textarea ref={blogText} name="create-blog" id="create-blog" placeholder='blogging here....'></textarea>
                        <button onClick={submitBlog} type='submit'>Create</button>
                    </form>
                </div>
                <div className="blogs">
                    <h3 className='head'>Your Blogs</h3>
                    <article>
                        {blogs ? "" : ""}
                    </article>
                </div>
            </div>
        </section>
    )
}

export default Profile
